CREATE TABLE order (`id` INT(16) NOT NULL AUTO_INCREMENT DEFAULT '' COMMENT 'autoincremental | 1 unique id for each order', `shop` VARCHAR(32) NULL DEFAULT '' COMMENT 'Shoping name', `shopId` INT(16) NULL DEFAULT '' COMMENT 'Shopping identifier', `order` VARCHAR(512) NULL DEFAULT '' COMMENT 'What people asked for', `shipDate` DATE() NULL DEFAULT '' COMMENT 'when people ordered it', `Status` VARCHAR(32) NULL DEFAULT 'pediente' COMMENT 'If it's delivered or not', `price` VARCHAR(16) NULL DEFAULT '' COMMENT 'total price of the order', `pickAddress` VARCHAR(32) NULL DEFAULT '' COMMENT 'shopping location', `deliveryAddress` VARCHAR(32) NULL DEFAULT '' COMMENT 'users location', `quarrelDescription` VARCHAR(512) NULL DEFAULT '' COMMENT 'Description if there was a problem', `quarrelPicture` VARCHAR(1024) NULL DEFAULT '' COMMENT 'base64 picture if there was a problem', `reviewDescription` VARCHAR(512) NULL DEFAULT '' COMMENT 'user's comments for shop reputation', `reviewLevel` INT(1) NULL DEFAULT '' COMMENT 'How many stars users gave', `deliveryId` INT(32) NULL DEFAULT '' COMMENT 'Id of the delivery guy', `deliveryMoney` VARCHAR(16) NULL DEFAULT '' COMMENT 'How much of price goes to delivery', `userId` INT(16) NULL DEFAULT '' COMMENT 'Id of the user that ordered')