CREATE TABLE user (`id` INT(16) NOT NULL AUTO_INCREMENT DEFAULT '' COMMENT 'autoincremental | 1 unique id for each user', `username` VARCHAR(32) NULL DEFAULT '' COMMENT 'Unique username for users', `firstName` VARCHAR(32) NULL DEFAULT '' COMMENT 'Users name', `lastName` VARCHAR(32) NULL DEFAULT '' COMMENT 'Users lastname', `email` VARCHAR(128) NULL DEFAULT '' COMMENT 'rsa | users email encoded by rsa', `password` VARCHAR(256) NULL DEFAULT '' COMMENT 'PASSWORD_BCRYPT | users password by bcrypt', `phone` VARCHAR(32) NULL DEFAULT '' COMMENT 'rsa | users phone encoded by rsa', `country` VARCHAR(32) NULL DEFAULT '' COMMENT 'users location', `state` VARCHAR(32) NULL DEFAULT '' COMMENT 'users location', `city` VARCHAR(32) NULL DEFAULT '' COMMENT 'users location', `rol` INT(8) NULL DEFAULT '' COMMENT 'worker / shop / normal user', `kyc` INT(2) NULL DEFAULT '0' COMMENT 'know the user', `tarjeta` VARCHAR(256) NULL DEFAULT '' COMMENT 'card wallet', `cuenta` VARCHAR(256) NULL DEFAULT '' COMMENT 'wallet in app', `admin` INT(2) NULL DEFAULT '0' COMMENT 'is it admin or a user', `priceKm` INT(16) NULL DEFAULT '' COMMENT 'price by km for workers', `zona1` VARCHAR(64) NULL DEFAULT '' COMMENT 'border to deliver (top left)', `zona2` VARCHAR(64) NULL DEFAULT '' COMMENT 'border to deliver (top right)', `zona3` VARCHAR(64) NULL DEFAULT '' COMMENT 'border to deliver  (bottom left)', `zona4` VARCHAR(64) NULL DEFAULT '' COMMENT 'border to deliver  (bottom right')